mapper.select:
select t1.*,t2.id a_id,t2.name,t2.introduction,t2.district_id,t2.district_name,
t2.city_id,t2.city_name,t2.province_id,t2.province_name,t2.address_detail,t2.latitude,
t2.longitude,t2.phone,t2.is_release a_is_release,t3.id is not null isCheckIn,lease_end_date
from (select * from room_info where is_deleted=0 and is_release=1) t1
inner join (select * from apartment_info where is_deleted=0

                        and province_Id=#{queryVo.provinceId}


                        and city_Id=#{queryVo.cityId}


                        and district_Id=#{queryVo.districtId}


                        and apartment_Id=#{queryVo.apartmentId}


                                         ) t2 on t1.apartment_id=t2.id
left join (select * from lease_agreement where is_deleted=0) t3 on t1.id=t3.room_id and status in(2,5,7)
