mapper.select:
select
    va.id v_id,va.user_id,va.name v_name,va.phone v_phone,va.apartment_id,va.appointment_time,
    va.additional_info,va.appointment_status,ai.*
from view_appointment va left join apartment_info ai
                                   on va.apartment_id=ai.id and ai.is_deleted=0

    and ai.province_id=#{queryVo.provinceId}


    and ai.city_id=#{queryVo.cityId}


    and ai.district_id=#{queryVo.districtId}

where va.is_deleted=0

    and va.apartment_id=#{queryVo.apartmentId}


    and va.name like concat('%',#{queryVo.name},'%')


    and va.phone like concat('%',#{queryVo.phone},'%')
